plugin.callback:
      unsigned long pos;
      float sig;
      const float fade = fabs(amp * smooth) + 0.0001;

      for (pos = 0; pos < sample_count; pos++) {
	sig = fabs(input[pos]) - amp;

        if (sig < 0.0f) {
          sig *= (1.0f + sig/fade) * smooth;
        }

	if (input[pos] < 0.0f) {
	  buffer_write(output[pos], -sig);
        } else {
	  buffer_write(output[pos], sig);
        }
      }
