plugin.callback:
unsigned long pos;

for (pos = 0; pos < sample_count; pos++) {
	const LADSPA_Data in = input[pos];

	if((in < CLIP) && (in > -CLIP)) {
		buffer_write(output[pos], in);
	} else if (in > 0.0f) {
		buffer_write(output[pos], MAX_AMP - (CLIP_A / (CLIP_B + in)));
	} else {
		buffer_write(output[pos], -(MAX_AMP - (CLIP_A / (CLIP_B - in))));
	}
}
