plugin.callback:
      unsigned long pos;
      float coef = pow(10.0f, gain * 0.05f) * 0.5f;
      float m, s;

      for (pos = 0; pos < sample_count; pos++) {
	m = lin[pos] + rin[pos];
	s = lin[pos] - rin[pos];
        buffer_write(lout[pos], m * coef + s * 0.5f);
        buffer_write(rout[pos], m * coef - s * 0.5f);
      }
