plugin.callback:
int pos;
float shape = 0.0f;

if (shapep < 1.0f && shapep > -1.0f) {
	shape = 1.0f;
} else if (shape < 0) {
	shape = -1.0f / shape;
} else {
	shape = shapep;
}

for (pos = 0; pos < sample_count; pos++) {
	if (input[pos] < 0.0f) {
		buffer_write(output[pos], -pow(-input[pos], shape));
	} else {
		buffer_write(output[pos], pow(input[pos], shape));
	}
}
